#!/usr/bin/python3

def gcd(a,b):
    """get the GCD of a and b"""
    if b == 0:
        return a
    else:
        return gcd(b, a % b)
def exgcd(a,b):
    """
    Extended Euclidean algorithm
    return d, x0, y0: x0*a + y0*b = d
    """
    if b == 0:
        return a, 1, 0
    else:
        d, x1, y1 = exgcd(b, a % b)
        x0, y0 = y1, x1 - (a//b) * y1
        return d, x0, y0
